import ErrorMessage from 'components/errorMessage';

const TagList = ({ tags }) => {
  if (!tags) {
    return <ErrorMessage />;
  }

  return (
    <ul className="tag-list">
      {tags.map((tag) => (
        <li key={tag} className="tag-default tag-pill tag-outline">
          {tag}
        </li>
      ))}
    </ul>
  );
};

export default TagList;
