import useFetch from 'hooks/useFetch';
import { useEffect, useContext, useState } from 'react';
import { Navigate, useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import ErrorMessage from 'components/errorMessage';
import Loading from 'components/loading';
import TagList from 'components/tagList';
import { CurrentUserContext } from 'contexts/currentUser';
import Comments from 'pages/article/components/comments';

const Article = () => {
  const slug = useParams().slug;
  const apiUrl = `/articles/${slug}`;
  const [
    {
      response: fetchArticleResponse,
      error: fetchArticleError,
      isLoading: fetchArticleIsLoading,
    },
    doFetch,
  ] = useFetch(apiUrl);
  const [{ response: deleteArticleResponse }, doDeleteArticle] =
    useFetch(apiUrl);
  const [isSuccessfulDelete, setIsSuccessfulDelete] = useState(false);
  const [currentUserState] = useContext(CurrentUserContext);

  const isAuthor = () => {
    if (!fetchArticleResponse || !currentUserState.isLoggedIn) {
      return false;
    }

    return (
      currentUserState.currentUser.username ===
      fetchArticleResponse.article.author.username
    );
  };

  useEffect(() => {
    doFetch();
  }, [doFetch]);

  const deleteArticle = () => {
    doDeleteArticle({ method: 'delete' });
  };

  useEffect(() => {
    if (deleteArticleResponse === null) {
      return;
    }

    setIsSuccessfulDelete(true);
  }, [deleteArticleResponse]);

  if (isSuccessfulDelete) {
    return <Navigate to="/" />;
  }

  return (
    <div className="article-page">
      <div className="banner">
        {!fetchArticleIsLoading && fetchArticleResponse && (
          <div className="container">
            <h1>{fetchArticleResponse.article.title}</h1>
            <div className="article-meta">
              <Link
                to={`/profiles/${fetchArticleResponse.article.author.username}`}
              >
                <img
                  src={fetchArticleResponse.article.author.image}
                  alt=""
                ></img>
              </Link>
              <div className="info">
                <Link
                  to={`/profiles/${fetchArticleResponse.article.author.username}`}
                >
                  {fetchArticleResponse.article.author.username}
                </Link>
                <span className="date">
                  {new Date(
                    fetchArticleResponse.article.createdAt
                  ).toLocaleString()}
                </span>
              </div>
              {isAuthor() && (
                <span>
                  <Link
                    className="btn btn-outline-secondary btn-sm"
                    to={`/articles/${fetchArticleResponse.article.slug}/edit`}
                  >
                    <i className="ion-edit" />
                    Edit Article
                  </Link>
                  <button
                    className="btn btn-outline-danger btn-sm"
                    onClick={deleteArticle}
                  >
                    <i className="ion-trash-a" />
                    Delete Article
                  </button>
                </span>
              )}
            </div>
          </div>
        )}
      </div>
      <div className="container">
        {fetchArticleIsLoading && <Loading />}
        {fetchArticleError && <ErrorMessage />}
        {!fetchArticleIsLoading && fetchArticleResponse && (
          <>
            <div className="row article-content">
              <div className="col-xs-12">
                <div>
                  <p>{fetchArticleResponse.article.body}</p>
                </div>
                <TagList tags={fetchArticleResponse.article.tagList} />
              </div>
            </div>
            <Comments slug={fetchArticleResponse.article.slug} />
          </>
        )}
      </div>
    </div>
  );
};

export default Article;
