import { useParams, NavLink } from 'react-router-dom';

import useFetch from 'hooks/useFetch';
import { useEffect, useContext } from 'react';
import UserArticles from 'pages/userProfile/components/userArticles';
import Follow from 'components/follow';
import { CurrentUserContext } from 'contexts/currentUser';

const UserProfile = ({ isFavoritesPage = false }) => {
  const slug = useParams().slug;
  const apiUrl = `/profiles/${slug}`;
  const [{ response }, doFetch] = useFetch(apiUrl);
  const [{ isLoggedIn }] = useContext(CurrentUserContext);

  useEffect(() => {
    doFetch();
  }, [doFetch, apiUrl]);

  if (!response) {
    return null;
  }

  return (
    <div className="profile-page">
      <div className="user-info">
        <div className="container">
          <div className="row">
            <div className="col-xs-12 col-md-10 offset-md-1">
              <img className="user-img" src={response.profile.image} alt="" />
              <h4>{response.profile.username}</h4>
              <p>{response.profile.bio}</p>
              {isLoggedIn && (
                <Follow
                  username={response.profile.username}
                  following={response.profile.following}
                />
              )}
            </div>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row">
          <div className="col-xs-12 col-md-10 offset-md-1">
            <div className="articles-toggle">
              <ul className="nav nav-pills outline-active">
                <li className="nav-item">
                  <NavLink
                    end
                    to={`/profiles/${response.profile.username}`}
                    className="nav-link"
                  >
                    My posts
                  </NavLink>
                </li>
                <li className="nav-item">
                  <NavLink
                    to={`/profiles/${response.profile.username}/favorites`}
                    className="nav-link"
                  >
                    Favorites Posts
                  </NavLink>
                </li>
              </ul>
            </div>
            <UserArticles
              username={response.profile.username}
              isFavoritesPage={isFavoritesPage}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
